import React, { useState } from "react"


const slides = [
    {
        UpperBox: {
            Heading: "Save & earn in US dollars.",
            SubTitle: "Protect your savings from local inflation.",
        },
        Icon: {
            src: usdIcon,
            alt: "",
        },
        LowerBox: {
            Heading: "Aspan converts your deposits into US dollar digital currencies.",
            SubTitle: " ",
        },
    },
    {
        UpperBox: {
            Heading: "Safe and Sound.",
            SubTitle: "Aspan secures your savings using blockchain technology.",
        },
        Icon: {
            src: lockIcon,
            alt: "",
        },
        LowerBox: {
            Heading: "Battle-tested methods and totally transparent.",
            SubTitle: " ",
        },
    },
    {
        UpperBox: {
            Heading: "Available anywhere, at any time.",
            SubTitle: "Aspan lets you deposit & withdraw from any country, no matter the hour.",
        },
        Icon: {
            src: worldIcon,
            alt: "",
        },
        LowerBox: {
            Heading:
                "Aspan connects you to decentralized finance, where no one authority is in control.",
            SubTitle: " ",
        },
    },
]

function Home(){

    const [currentSlide, setCurrentSlide] = useState(0)
    const navigate = useNavigate()

    const prevSlide = () => {
        if (currentSlide === 0) navigate("../")
        setCurrentSlide(currentSlide - 1)
    }

    const nextSlide = () => {
        if (currentSlide === slides.length - 1) navigate("../createAccount")
        setCurrentSlide(currentSlide + 1)
    }



    let filledStorySlides = []
    let unfilledStorySlides = []
    for (let i = currentSlide; i >= 0; i--) {
        filledStorySlides.push(" ")
    }
    for (let i = currentSlide; i < slides.length - 1; i++) {
        unfilledStorySlides.push(" ")
    }

    return (
        <WelcomeSlide
            slide={slides[currentSlide]}
            prevSlide={prevSlide}
            nextSlide={nextSlide}
            filledStorySlides={filledStorySlides}
            unfilledStorySlides={unfilledStorySlides}
            setSlide={setCurrentSlide}
            currentSlide={currentSlide}
        />
    );
}
export default Home;
